sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// for sn debugger test
	var gsLocal = gs;

	gs.info("running needit business rule test.");

	// rightnow stores the current time
	var rightnow = new GlideDateTime();
	var dateToday = rightnow.getDate();//sj9696

	// Create a GlideDateTime object for the When needed date
	var whenNeeded = new GlideDateTime(current.u_when_needed);
	var dateWhenNeeded = whenNeeded.getDate();//sj9696

	// If the When needed date is before rightnow, do not write the record to the database
	// Output an error message to the screen

	if(dateWhenNeeded == dateToday){//sj9696
		gs.addErrorMessage("When needed date is today. It must be at least tommorrow.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	} else if(whenNeeded.before(rightnow)){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}


})(current, previous);
